mapper.select:
SELECT u.user_id user_id, u.user_name user_name, u.user_pwd user_pwd, u.user_mail user_mail, u.user_phonenumber user_phonenumber, u.user_pic user_pic, u.register_date register_date, ur.role_name role_name, ul.lv_name lv_name FROM user u, user_role ur, user_level ul WHERE u.role_id = ur.role_id AND u.lv_id = ul.lv_id
AND u.user_name = #{user_name}
AND u.user_mail = #{user_mail}
AND u.user_phonenumber = #{user_phonenumber}
